import React, { Fragment, useEffect, useState } from 'react'
import EditTodo from './Edit'

const ListTodo = () => {
    const [todos, setTodos] = useState([])

    const deleteTodo = async id => {
        try {
            const deleteTodo = await fetch(
            // utilizamos crase quando precisamos inserir um valor de script dentro da string
                `http://localhost:5050/todos/${id}`, 
                {
                    method: 'DELETE'
                }
            ) 

            setTodos(todos.filter(todo => todo.todo_id !== id))
        } catch (error) {
            console.log(error)
        }
    }

    const getTodos = async () => {
        try{
            const response = await fetch('http://localhost:5050/todos/')
            const jsonData = await response.json()

            setTodos(jsonData)
        } catch (error) {
            console.log(error)
        }
    }

    useEffect(() => {
        getTodos()
    }, [])

    document.querySelector('input[type=checkbox]:checked').document.querySelector('.todo-description').style.textDecoration = 'line-through'


    return (
        <Fragment>
            <table className='table mt-5 text-center'>
                <thead>
                    <tr>
                        <th></th>
                        <th>Descrição</th>
                        <th>Editar</th>
                        <th>Deletar</th>
                    </tr>
                </thead>
                <tbody>
                    {todos.map(todo => (
                        <tr key={todo.todo_id}>
                            <td><input type='checkbox'/></td>
                            <td className='todo-description'>{todo.description}</td>
                            <td>
                                <EditTodo todo={todo} />
                            </td>
                            <td>
                                <button 
                                className='btn btn-danger'
                                onClick={() => deleteTodo(todo.todo_id)}
                                >
                                    Deletar
                                </button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </Fragment>
    )
}

export default ListTodo;
