import React, { Fragment, useState } from 'react';

const InputTodo = () => {
    const [description, setDescription] = useState('')

    const Submiting = async e => {
        e.preventDefault()
        try{
            const body = { description }
            const response = await fetch('http://localhost:5050/todos', {
                method: 'POST',
                headers: {'Content-Type':'application/json'},
                body: JSON.stringify(body)
            })
            window.location = "/"
        } catch (error) {
            console.log(error)
        }
    }

    return (
        <Fragment>
            <h1 className='text-center mt-5'>Afazeres</h1>
            <form className='f-flex mt-5' onSubmit={Submiting}>
                <div class="input-group mb-3">
                    <input type="text" class="form-control" placeholder="Recipient's username" aria-label="Recipient's username" aria-describedby="button-addon2"/>
                    <button class="btn btn-outline-secondary" type="button" id="button-addon2">Button</button>
                </div>
                <input  
                    type='text' 
                    class="form-control"
                    // className='form-control' 
                    value={description} 
                    
                    onChange={e => setDescription(e.target.value)}
                />
                <button className='btn btn-success' type="button">Adicionar</button>
            </form>
        </Fragment>
    )
}
export default InputTodo
