import React, { Fragment, useEffect, useState } from 'react';
import EditTodo from './Edit';

/**
 * Componente ListTodo:
 * Este componente exibe uma lista de afazeres e afazeres concluídos em duas tabelas separadas.
 * Permite a marcação de afazeres como completos, edição e exclusão de afazeres.
 */
const ListTodo = () => {
  // Estado para armazenar todos os afazeres não concluídos
  const [todos, setTodos] = useState([]);
  // Estado para armazenar todos os afazeres concluídos
  const [completedTodos, setCompletedTodos] = useState([]);
  // Estado para armazenar afazeres em progresso
  const [inProgressTodos, setInProgressTodos] = useState([]);



  // Função para deletar um afazer
  const deleteTodo = async (id) => {
    try {
      // Chamada para excluir o afazer no servidor
      const deleteTodo = await fetch(`http://localhost:5050/todos/${id}`, {
        method: 'DELETE',
      });

      // Atualiza o estado removendo o afazer excluído
      setTodos(todos.filter((todo) => todo.todo_id !== id));

      // Verifica se o afazer excluído está na lista de completos e o remove
      const isCompleted = todos.find((todo) => todo.todo_id === id)?.completed;
      if (isCompleted) {
        setCompletedTodos(completedTodos.filter((todo) => todo.todo_id !== id));
      }
    } catch (error) {
      console.log(error);
    }
  };

  // Função para obter todos os afazeres do servidor
  const getTodos = async () => {
    try {
      // Chamada para obter a lista de afazeres do servidor
      const response = await fetch('http://localhost:5050/todos/');
      // Converte a resposta para formato JSON
      const jsonData = await response.json();

      // Separa os afazeres completos e não completos
      const completed = jsonData.filter((todo) => todo.completed);
      const notCompleted = jsonData.filter((todo) => !todo.completed);

      // Atualiza os estados com os afazeres completos e não completos
      setTodos(notCompleted);
      setCompletedTodos(completed);
    } catch (error) {
      console.log(error);
    }
  };

  // Função para lidar com a mudança do estado do checkbox
 // Função para lidar com a mudança do estado do select
const handleSelectChange = async (id, selectedStatus) => {
  try {
    // Chamada para atualizar o status do afazer no servidor
    const response = await fetch(`http://localhost:5050/todos/${id}`, {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ completed: selectedStatus === 'Feito' }),
    });

    // Atualiza o estado do afazer com o novo status
    const updatedTodos = todos.map((todo) =>
      todo.todo_id === id ? { ...todo, completed: selectedStatus === 'Feito' } : todo
    );

    // Atualiza o estado dos afazeres
    setTodos(updatedTodos);

    // Atualiza a lista de completos se necessário
    if (selectedStatus === 'Feito') {
      setCompletedTodos([...completedTodos, updatedTodos.find((todo) => todo.todo_id === id)]);
    } else {
      setCompletedTodos(completedTodos.filter((todo) => todo.todo_id !== id));
    }
  } catch (error) {
    console.log(error);
  }
};


  // Efeito de lado para obter os afazeres ao montar o componente
  useEffect(() => {
    getTodos();
  }, []);

  // Renderização do componente
  return (
    <Fragment>
      {/* Tabela de Afazeres */}
      <div className='List'> 

    {/* Verifica se há afazeres não concluídos antes de renderizar a tabela */}
      <div className='todo-list'>
        <h3 className='text-center mt-5'>Afazeres</h3>
          <table className='table mt-5 text-center'>
            <thead>
              <tr>
                <th></th>
                <th>Descrição</th>
                <th>Editar</th>
                <th>Deletar</th>
              </tr>
            </thead>
            <tbody>
                {/* Mapeia e renderiza afazeres não completos */}
                {todos.map((todo) => (
                  !todo.completed && (
                    <tr key={todo.todo_id}>
                      <td>
                        {/* Campo select para escolher o status */}
                        <select
                          value={todo.completed ? 'Feito' : 'À fazer'}
                          onChange={(e) => handleSelectChange(todo.todo_id, e.target.value)}
                        >
                          <option value="Feito">Feito</option>
                          <option value="Em progresso">Em progresso</option>
                          <option value="À fazer">À fazer</option>
                        </select>
                      </td>
                      {/* Descrição do afazer */}
                      <td className={todo.completed ? 'todo-description completed' : 'todo-description'}>
                        {todo.description}
                      </td>
                      {/* Componente para editar o afazer */}
                      <td>
                        <EditTodo todo={todo} />
                      </td>
                      {/* Botão para deletar o afazer */}
                      <td>
                        <button
                          className='btn btn-danger'
                          onClick={() => deleteTodo(todo.todo_id)}
                        >
                          Deletar
                        </button>
                      </td>
                    </tr>
                  )
                ))}
            </tbody>
          </table>
      </div>


{/* Tabela de Afazeres Concluídos */}
{completedTodos.length > 0 && (
  <div className='done todo-list'>
    <h3 className='text-center mt-5'>Feito</h3>
    <table className='done table mt-5 text-center'>
      <thead>
        <tr>
          <th></th>
          <th>Descrição</th>
          <th>Editar</th>
          <th>Deletar</th>
        </tr>
      </thead>
      <tbody>
        {/* Mapeia e renderiza afazeres completos como "Feito" */}
        {completedTodos.map((todo) => (
          todo.completed && (
            <tr key={todo.todo_id}>
              <td>
                        {/* Campo select para escolher o status */}
                        <select
                          value={todo.completed ? 'Feito' : 'À fazer'}
                          onChange={(e) => handleSelectChange(todo.todo_id, e.target.value)}
                        >
                          <option value="Feito">Feito</option>
                          <option value="Em progresso">Em progresso</option>
                          <option value="À fazer">À fazer</option>
                        </select>
                      </td>
              {/* Descrição do afazer completo */}
              <td className='todo-description completed'>{todo.description}</td>
              {/* Componente para editar o afazer */}
              <td>
                <EditTodo todo={todo} />
              </td>
              {/* Botão para deletar o afazer */}
              <td>
                <button
                  className='btn btn-danger'
                  onClick={() => deleteTodo(todo.todo_id)}
                >
                  Deletar
                </button>
              </td>
            </tr>
          )
        ))}
      </tbody>
    </table>
  </div>
)}

{/* Tabela de Afazeres Em Progresso */}
{inProgressTodos.length > 0 && (
  <div className='in-progress todo-list'>
    <h3 className='text-center mt-5'>Em Progresso</h3>
    <table className='in-progress table mt-5 text-center'>
      <thead>
        <tr>
          <th></th>
          <th>Descrição</th>
          <th>Editar</th>
          <th>Deletar</th>
        </tr>
      </thead>
      <tbody>
        {/* Mapeia e renderiza afazeres em progresso */}
        {inProgressTodos.map((todo) => (
          !todo.completed && (
            <tr key={todo.todo_id}>
              <td>
                        {/* Campo select para escolher o status */}
                        <select
                          value={todo.completed ? 'Feito' : 'À fazer'}
                          onChange={(e) => handleSelectChange(todo.todo_id, e.target.value)}
                        >
                          <option value="Feito">Feito</option>
                          <option value="Em progresso">Em progresso</option>
                          <option value="À fazer">À fazer</option>
                        </select>
                      </td>
              {/* Descrição do afazer em progresso */}
              <td className='todo-description'>{todo.description}</td>
              {/* Componente para editar o afazer */}
              <td>
                <EditTodo todo={todo} />
              </td>
              {/* Botão para deletar o afazer */}
              <td>
                <button
                  className='btn btn-danger'
                  onClick={() => deleteTodo(todo.todo_id)}
                >
                  Deletar
                </button>
              </td>
            </tr>
          )
        ))}
      </tbody>
    </table>
  </div>
)}


      </div>
    </Fragment>
  );
};

export default ListTodo;
