import React, { Fragment, useEffect, useState } from 'react';
import EditTodo from './Edit';

const ListTodo = () => {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);

  const deleteTodo = async (id) => {
    try {
      const deleteTodo = await fetch(`http://localhost:5050/todos/${id}`, {
        method: 'DELETE',
      });

      // Verifique se o todo excluído está na lista de completos
      const isCompleted = todos.find((todo) => todo.todo_id === id)?.completed;

      setTodos(todos.filter((todo) => todo.todo_id !== id));

      // Se estiver completo, remova-o da lista de completos
      if (isCompleted) {
        setCompletedTodos(completedTodos.filter((todo) => todo.todo_id !== id));
      }
    } catch (error) {
      console.log(error);
    }
  };

  const getTodos = async () => {
    try {
      const response = await fetch('http://localhost:5050/todos/');
      const jsonData = await response.json();

      // Separe os todos concluídos e não concluídos
      const completed = jsonData.filter((todo) => todo.completed);
      const notCompleted = jsonData.filter((todo) => !todo.completed);

      setTodos(notCompleted);
      setCompletedTodos(completed);
    } catch (error) {
      console.log(error);
    }
  };

  const handleCheckboxChange = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.todo_id === id ? { ...todo, completed: !todo.completed } : todo
    );

    setTodos(updatedTodos);

    // Se o todo agora estiver completo, mova-o para a lista de completos
    const todoToUpdate = updatedTodos.find((todo) => todo.todo_id === id);
    if (todoToUpdate.completed) {
      setCompletedTodos([...completedTodos, todoToUpdate]);
    } else {
      // Se não estiver completo, remova-o da lista de completos (se estiver lá)
      setCompletedTodos(completedTodos.filter((todo) => todo.todo_id !== id));
    }
  };

  useEffect(() => {
    getTodos();
  }, []);

  return (
    <Fragment>
      <table className='table mt-5 text-center'>
        <thead>
          <tr>
            <th></th>
            <th>Descrição</th>
            <th>Editar</th>
            <th>Deletar</th>
          </tr>
        </thead>
        <tbody>
          {todos.map((todo) => (
            <tr key={todo.todo_id}>
              <td>
                <input
                  type='checkbox'
                  id={`checkbox-${todo.todo_id}`}
                  checked={todo.completed}
                  onChange={() => handleCheckboxChange(todo.todo_id)}
                />
              </td>
              <td className={todo.completed ? 'todo-description completed' : 'todo-description'}>
                {todo.description}
              </td>
              <td>
                <EditTodo todo={todo} />
              </td>
              <td>
                <button
                  className='btn btn-danger'
                  onClick={() => deleteTodo(todo.todo_id)}
                >
                  Deletar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <h3>Realizados</h3>

      <table className='done table mt-5 text-center'>
        <thead>
          <tr>
            <th></th>
            <th>Descrição</th>
            <th>Editar</th>
            <th>Deletar</th>
          </tr>
        </thead>
        <tbody>
          {completedTodos.map((todo) => (
            <tr key={todo.todo_id}>
              <td>
                <input
                  type='checkbox'
                  id={`checkbox-${todo.todo_id}`}
                  checked={todo.completed}
                  onChange={() => handleCheckboxChange(todo.todo_id)}
                />
              </td>
              <td className='todo-description completed'>{todo.description}</td>
              <td>
                <EditTodo todo={todo} />
              </td>
              <td>
                <button
                  className='btn btn-danger'
                  onClick={() => deleteTodo(todo.todo_id)}
                >
                  Deletar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </Fragment>
  );
};

export default ListTodo;
