import React, { Fragment, useEffect, useState } from 'react';
import EditTodo from './Edit';

const ListTodo = () => {
  const [todos, setTodos] = useState([]);
  const [completedTodos, setCompletedTodos] = useState([]);
  const [inProgressTodos, setInProgressTodos] = useState([]);
  const [todoTodos, setTodoTodos] = useState([]);

  useEffect(() => {
    getTodos();
  }, []);

  const deleteTodo = async (id) => {
    try {
      const deleteTodo = await fetch(`http://localhost:5050/todos/${id}`, {
        method: 'DELETE',
      });

      setTodos(todos.filter((todo) => todo.todo_id !== id));
      updateDerivedStates(); // Atualiza os estados derivados após a exclusão
    } catch (error) {
      console.log(error);
    }
  };

  const getTodos = async () => {
    try {
      const response = await fetch('http://localhost:5050/todos/');
      const jsonData = await response.json();

      setTodos(jsonData);
      updateDerivedStates(); // Atualiza os estados derivados após a obtenção dos afazeres
    } catch (error) {
      console.log(error);
    }
  };

  const updateDerivedStates = () => {
    const updatedCompletedTodos = todos.filter((todo) => todo.completed);
    const updatedInProgressTodos = todos.filter((todo) => !todo.completed);
    const updatedTodoTodos = updatedInProgressTodos.filter((todo) => !inProgressTodos.includes(todo));

    setCompletedTodos(updatedCompletedTodos);
    setInProgressTodos(updatedInProgressTodos);
    setTodoTodos(updatedTodoTodos);
  };



  const handleSelectChange = async (id, selectedStatus) => {
    try {
      const response = await fetch(`http://localhost:5050/todos/${id}`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ completed: selectedStatus === 'Feito' }),
      });

      const updatedTodos = todos.map((todo) =>
        todo.todo_id === id ? { ...todo, completed: selectedStatus === 'Feito' } : todo
      );

      setTodos(updatedTodos);
      updateDerivedStates(); // Atualiza os estados derivados após a mudança de status

    } catch (error) {
      console.log(error);
    }
  };

  // Renderização do componente
  return (
    <Fragment>
      <div className='List'>
        <div className='todo-list'>
          <h3 className='text-center mt-5'>À fazer</h3>
          <table className='table mt-5 text-center'>
            <thead>
              <tr>
                <th></th>
                <th>Descrição</th>
                <th>Editar</th>
                <th>Deletar</th>
              </tr>
            </thead>
            <tbody>
              {todoTodos.map((todo) => (
                <tr key={todo.todo_id}>
                  <td>
                    <select
                      value='À fazer'
                      onChange={(e) => handleSelectChange(todo.todo_id, e.target.value)}
                    >
                      <option value="Feito">Feito</option>
                      <option value="Em progresso">Em progresso</option>
                      <option value="À fazer">À fazer</option>
                    </select>
                  </td>
                  <td className='todo-description'>{todo.description}</td>
                  <td>
                    <EditTodo todo={todo} />
                  </td>
                  <td>
                    <button
                      className='btn btn-danger'
                      onClick={() => deleteTodo(todo.todo_id)}
                    >
                      Deletar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className='done todo-list'>
          <h3 className='text-center mt-5'>Feito</h3>
          <table className='done table mt-5 text-center'>
            <thead>
              <tr>
                <th></th>
                <th>Descrição</th>
                <th>Editar</th>
                <th>Deletar</th>
              </tr>
            </thead>
            <tbody>
              {completedTodos.map((todo) => (
                <tr key={todo.todo_id}>
                  <td>
                    <select
                      value='Feito'
                      onChange={(e) => handleSelectChange(todo.todo_id, e.target.value)}
                    >
                      <option value="Feito">Feito</option>
                      <option value="Em progresso">Em progresso</option>
                      <option value="À fazer">À fazer</option>
                    </select>
                  </td>
                  <td className='todo-description completed'>{todo.description}</td>
                  <td>
                    <EditTodo todo={todo} />
                  </td>
                  <td>
                    <button
                      className='btn btn-danger'
                      onClick={() => deleteTodo(todo.todo_id)}
                    >
                      Deletar
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>

        <div className='in-progress todo-list'>
        <h3 className='text-center mt-5'>Em Progresso</h3>
        <table className='in-progress table mt-5 text-center'>
          <thead>
            <tr>
              <th></th>
              <th>Descrição</th>
              <th>Editar</th>
              <th>Deletar</th>
            </tr>
          </thead>
          <tbody>
            {inProgressTodos.map((todo) => (
              <tr key={todo.todo_id}>
                <td>
                  <select
                    value='Em progresso'
                    onChange={(e) => handleSelectChange(todo.todo_id, e.target.value)}
                  >
                    <option value="Feito">Feito</option>
                    <option value="Em progresso">Em progresso</option>
                    <option value="À fazer">À fazer</option>
                  </select>
                </td>
                <td className='todo-description'>{todo.description}</td>
                <td>
                  <EditTodo todo={todo} />
                </td>
                <td>
                  <button
                    className='btn btn-danger'
                    onClick={() => deleteTodo(todo.todo_id)}
                  >
                    Deletar
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      </div>
    </Fragment>
  );
};

export default ListTodo;
